			  <h4 class="heading-title">Treatment Effect   <a href="#" data-toggle="modal" data-target="#myModa20"><i class="fa fa-question-circle"></i></a>
			  </h4>
			 </div>

			 <!-- Modal -->
              <div class="modal fade" id="myModa20" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">
                <div class="modal-dialog modal-lg" role="document">
